const GameList = () => {
  const [games, setGames] = useState([]);
  const [filterActivated, setFilterActivated] = useState(false);
  const [gameDeleted, setGameDeleted] = useState([]);

  useEffect(() => {
    axios
      .get("https://apis.wilders.dev/wild-games/games/")
      .then((res) => setGames(res.data));
  }, []);

  useEffect(() => {
    setGames(games.filter((game) => game.id !== gameDeleted));
  }, [gameDeleted]);

  return (
    <div>
      <button
        className="games-button"
        onClick={() => setFilterActivated(!filterActivated)}
      >
        {filterActivated ? "All games" : "Best games"}
      </button>
      <div className="games-container">
        <div className="game-container">
          <Game
            key={game.id}
            id={game.id}
            name={game.name}
            released={game.released}
            rating={game.rating}
            image={game.background_image}
            setGameDeleted={setGameDeleted}
          />
        </div>
      </div>
    </div>
  );
};

export default GameList;
