import { Link } from "react-router-dom";

import "../style/Game.css";

const Game = ({ name, released, rating, image, id, setGameDeleted }) => {
  const urlId = `/games/${id}`;
  return (
    <div className="game-card">
      <h2>{name}</h2>
      <h3>Rating: {rating}</h3>
      <h4>Release: {released}</h4>
      <img className="game-photo" src={image} alt={name} />
      <button className="game-button" onClick={() => setGameDeleted(id)}>
        Delete
      </button>
    </div>
  );
};

export default Game;
