import { Link } from "react-router-dom";

import "../style/Game.css";

const Game = ({ name, released, rating, image, id, toDelete }) => {
  const cardUrl = `/games/${id}`;

  return (
    <div className="game-card">
      <Link to={cardUrl} className="game-link">
        <h2>{name}</h2>
        <h3>Rating: {rating}</h3>
        <h4>Release: {released}</h4>
        <img className="game-photo" src={image} alt={name} />
      </Link>
      <button className="game-button" onClick={() => setDeleteGame(id)}>
        Delete
      </button>
    </div>
  );
};

export default Game;
